mapper.select:
SELECT rb1.IB_DEAL_NO , rb1.DSC_DT , rb1.IB_DEAL_NM , rb1.PTCP_AMT , rb1.WRT_DT , rb1.MTRT_DT , rb6.HDQT_NM AS HDQT_CD , rb6.DPRT_NM AS DPRT_CD , rb6.EMP_NM AS CHRG_P_ENO , rb2.CD_VL_NM AS COPRTN_TYP_CD , rb1.TL_ERN_AMT , rb1.RCVBL_ERN_AMT , rb1.WRT_ERN_AMT , rb1.ENTP_RNM , rb3.CD_VL_NM AS CRDT_GRD_CD , rb1.GDS_DVD_1_NM , rb1.GDS_DVD_2_NM , rb1.GDS_DVD_3_NM , rb1.GDS_DVD_4_NM , rb4.CD_VL_NM AS INVST_NTN_CD , rb5.CD_VL_NM AS INVST_CRNCY_CD , rb1.CRNCY_AMT FROM RAA01B rb1 LEFT JOIN RAA99A rb6 ON rb1.CHRG_P_ENO = rb6.ENO , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM FROM RAA90B rb1 , RAA91B rb2 WHERE 1=1 AND rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP AND rb1.CMNS_CD_NM = 'COPRTN_TYP_CD' ) rb2 , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM FROM RAA90B rb1 , RAA91B rb2 WHERE 1=1 AND rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP AND rb1.CMNS_CD_NM = 'CRDT_GRD_CD' ) rb3 , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM FROM RAA90B rb1 , RAA91B rb2 WHERE 1=1 AND rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP AND rb1.CMNS_CD_NM = 'CNTY_CD' ) rb4 , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM FROM RAA90B rb1 , RAA91B rb2 WHERE 1=1 AND rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP AND rb1.CMNS_CD_NM = 'INVST_CRNCY_CD' ) rb5
WHERE 1=1 AND rb1.COPRTN_TYP_CD = rb2.CD_VL_ID AND rb1.CRDT_GRD_CD = rb3.CD_VL_ID AND rb1.INVST_NTN_CD = rb4.CD_VL_ID AND rb1.INVST_CRNCY_CD = rb5.CD_VL_ID
AND rb1.DSC_DT >= #{start}
AND rb1.DSC_DT IS NOT NULL AND rb1.DSC_DT != "" AND rb1.DSC_DT <= #{end}
AND rb1.DPRT_CD = #{dprtCd}
AND rb1.INVST_CRNCY_CD = #{invstCrncyCd}
AND rb1.IB_DEAL_NM LIKE CONCAT('%', #{dealNm}, '%')
