mapper.select:
SELECT rb1.IB_DEAL_NO AS IB_DEAL_NO , rb2.CD_VL_NM AS RISK_INSPCT_CCD , rb3.CD_VL_NM AS LST_C_CASE_CCD , rb5.EMP_NM AS CHRG_P_ENO , rb4.CD_VL_NM AS INSPCT_PRGRS_ST_CD , rb1.IB_DEAL_NM AS IB_DEAL_NM FROM RAA02B rb1 LEFT JOIN RAA99A rb5 ON rb1.CHRG_P_ENO = rb5.ENO , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM from RAA90B rb1 , RAA91B rb2 WHERE rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP and rb1.CMNS_CD_NM = 'RISK_INSPCT_CCD' ) rb2 , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM from RAA90B rb1 , RAA91B rb2 WHERE rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP and rb1.CMNS_CD_NM = 'LST_C_CASE_CCD' ) rb3 , ( SELECT rb2.CD_VL_ID , rb2.CD_VL_NM from RAA90B rb1 , RAA91B rb2 WHERE rb1.CMNS_CD_GRP = rb2.CMNS_CD_GRP and rb1.CMNS_CD_NM = 'INSPCT_PRGRS_ST_CD' ) rb4 WHERE 1=1 AND rb1.RISK_INSPCT_CCD = rb2.CD_VL_ID AND rb1.LST_C_CASE_CCD = rb3.CD_VL_ID AND rb1.INSPCT_PRGRS_ST_CD = rb4.CD_VL_ID
AND IB_DEAL_NO LIKE #{ibDealNo}
AND RA_DEAL_CCD LIKE #{raDealCcd}

SELECT LPAD (
             NVL( MAX(RA_DEAL_SQ), 0 ) + 1
             , 4, 0
            ) AS RA_DEAL_SQ
  FROM RAA02B
 WHERE 1=1
   AND RA_DEAL_CCD = #{raDealCcd}
   AND DPRT_CD = #{dprtCd}
