mapper.update:
    	UPDATE RAA03B
    	   SET BSC_ASTS_KND_CD			= #{bscAstsKndCd     }		
			 , BSC_ASTS_CNTNT			= #{bscAstsCntnt     }		
			 , OPN_PRC_VAL_AMT			= #{opnPrcValAmt     }		
			 , BSC_ASTS_ISNG_CORP_NO	= #{bscAstsIsngCorpNo}		



			 , RNMCNO					= #{rnmcno           }		
			 , HNDL_DY_TM				= CURRENT_TIMESTAMP()
			 , HNDL_DPRT_CD				= #{hndlDprtCd       }
			 , HNDL_P_ENO				= #{hndlPEno         }
		 WHERE 1=1
		   AND IB_DEAL_NO				= #{ibDealNo         }
		   AND RISK_INSPCT_CCD			= #{riskInspctCcd    }
		   AND LST_C_CASE_CCD			= #{lstCCaseCcd      }
		   AND ITEM_SQ					= #{itemSq           }

mapper.insert:
    	INSERT INTO RAA03B
    		   (
    		    IB_DEAL_NO							
				, RISK_INSPCT_CCD					
				, LST_C_CASE_CCD					
				, ITEM_SQ							
				, BSC_ASTS_KND_CD					
				, BSC_ASTS_CNTNT					
				, OPN_PRC_VAL_AMT					
				, BSC_ASTS_ISNG_CORP_NO				
				, INVST_CRNCY_CD					
				, CRNCY_AMT							
				, APLC_EXCH_R						
				, RNMCNO							
				, HNDL_DY_TM						
				, HNDL_DPRT_CD						
				, HNDL_P_ENO						
    		   )
		VALUES (
				#{ibDealNo         }				
				, #{riskInspctCcd    }				
				, #{lstCCaseCcd      }				
				, NEXT VALUE FOR RAA03B_SQ			
				, #{bscAstsKndCd    }				
				, #{bscAstsCntnt     }				
				, IFNULL(#{opnPrcValAmt}, 0)		
				, #{bscAstsIsngCorpNo}				
				, #{invstCrncyCd     }				
				, IFNULL(#{crncyAmt}, 0)			
				, IFNULL(#{aplcExchR}, 0)			
				, #{rnmcno           }				
				, CURRENT_TIMESTAMP()				
				, #{hndlDprtCd       }				
				, #{hndlPEno         }				
			   )

mapper.delete:
DELETE FROM RAA03B 
 WHERE 1=1
   AND IB_DEAL_NO = #{ibDealNo}
   AND ITEM_SQ = #{itemSq}
