mapper.select:
/* 권한별 메뉴화면 사용권한 조회 */ SELECT RGHT_CD , SQ , MENU_ID , MDFY_RGHT_CCD , HNDL_DY_TM , HNDL_DPRT_CD , HNDL_P_ENO FROM RAA95B WHERE 1=1
AND MENU_ID = #{lv2Id}
AND MENU_ID = #{lv3Id}

mapper.insert:

   INSERT INTO RAA95B (
		  RGHT_CD
		, SQ
		, MENU_ID
		, MDFY_RGHT_CCD
		, HNDL_DY_TM
		, HNDL_DPRT_CD
		, HNDL_P_ENO
		)
   VALUES (
		  #{rghtCd}
		, #{sq}
		, #{menuId}
		, #{mdfyRghtCcd}
		, DATE_FORMAT(NOW(),'%Y-%m-%d %H:%m:%s')
		, #{hndlDprtCd}
		, #{hndlPEno}
		)

mapper.update:
UPDATE RAA95B

	 MDFY_RGHT_CCD = #{mdfyRghtCcd}
	 , HNDL_DY_TM = #{hndlDyTm}
	 , HNDL_DPRT_CD = #{hndlDprtCd}
	 , HNDL_P_ENO = #{hndlPEno}
