mapper.select:
/* 권한별 메뉴화면 사용권한 조회 */ SELECT RGHT_CD , SQ , MENU_ID , MDFY_RGHT_CCD , HNDL_DY_TM , HNDL_DPRT_CD , HNDL_P_ENO FROM RAA95B WHERE 1=1
AND MENU_ID = #{lv2Id}
AND MENU_ID = #{lv3Id}

mapper.insert:
INSERT INTO RAA95B (
		RGHT_CD
		, SQ
		, MENU_ID
		, MDFY_RGHT_CCD
		, HNDL_DY_TM
		, HNDL_DPRT_CD
		, HNDL_P_ENO
		)
VALUES (
		#{rghtCd}
		, #{sq}
		, #{menuId}
		, #{mdfyRghtCcd}
		, NOW()
		, #{hndlDprtCd}
		, #{hndlPEno}
		)

mapper.update:
UPDATE RAA95B

	   MDFY_RGHT_CCD 	= #{mdfyRghtCcd}
	 , HNDL_DY_TM 		= NOW()
	 , HNDL_DPRT_CD 	= #{hndlDprtCd}
	 , HNDL_P_ENO 		= #{hndlPEno}

 WHERE SQ 				= #{sq}
   AND RGHT_CD 			= #{rghtCd}

mapper.select:
SELECT *
  FROM RAA95B
 WHERE RGHT_CD = #{rghtCd}
   AND MENU_ID = #{menuId}

mapper.update:
UPDATE RAA95B


		rght_cd = #{rghtCd},


		menu_id = #{menuId},


		mdfy_rght_ccd = #{mdfyRghtCcd},

	hndl_dy_tm = now(),
	hndl_p_eno = #{hndlPEno}

WHERE rght_cd = #{rghtCd}
  AND menu_id = #{menuId}

mapper.delete:
DELETE
  FROM RAA95B
 WHERE rght_cd = #{rghtCd}
   AND menu_id = #{menuId}
